mapper.select:
SELECT 
    e.id, b.id AS budget_id, e.user_id, e.amount, e.day, 
    e.description, e.version, c.name, c.code
FROM (
    /* 1. 여기서 인덱스를 타고 10개만 먼저 뽑습니다 (빛의 속도) */
    SELECT *
    FROM expense
    WHERE user_id = #{userId}
    AND year = #{year}
    AND month = #{month}

        AND (day < #{lastDay} 
            OR (day = #{lastDay} AND id < #{lastId}))

    ORDER BY day DESC, id DESC
    LIMIT #{size}
) e
/* 2. 딱 10개에 대해서만 조인을 수행합니다 (부하 거의 없음) */
LEFT JOIN budget b ON b.id = e.budget_id
LEFT JOIN category c ON c.code = b.category_code

SELECT b.category_code as code, e_sum.total_amount as totalAmount
FROM (
    SELECT budget_id, SUM(amount) as total_amount
    FROM expense
    WHERE user_id = #{userId} AND year = #{year} AND month = #{month}
    GROUP BY budget_id
) e_sum
JOIN budget b ON b.id = e_sum.budget_id;

mapper.update:
UPDATE expense
SET amount = #{amount},
    description = #{description},
    year = #{year},
    month = #{month},
    day = #{day},
    version = version + 1
WHERE id = #{id}
  AND user_id = #{userId}
  AND version = #{version}

mapper.select:
SELECT 
    CASE 
        WHEN (
            (SELECT COALESCE(SUM(amount), 0) 
            FROM expense 
            WHERE budget_id = #{budgetId} 
            AND year = #{year} 
            AND month = #{month})
            + #{amount}
        ) > (SELECT amount FROM budget WHERE id = #{budgetId})
        THEN 1
        ELSE 0
    END AS overBudget

SELECT COUNT(*)
FROM expense
WHERE budget_id = #{budgetId}
